package config;import java.net.MalformedURLException;import java.net.URL;import org.openqa.selenium.WebDriver;import org.openqa.selenium.chrome.ChromeDriver;import org.openqa.selenium.chrome.ChromeOptions;import org.openqa.selenium.edge.EdgeOptions;import org.openqa.selenium.firefox.FirefoxDriver;import org.openqa.selenium.firefox.FirefoxOptions;import org.openqa.selenium.ie.InternetExplorerDriver;import org.openqa.selenium.ie.InternetExplorerOptions;import org.openqa.selenium.opera.OperaOptions;import org.openqa.selenium.remote.RemoteWebDriver;public class Drivers {	/*	 * Esta classe cria a conexão com o servidor selenium, dibonibilizando diversos tipos de conexão.	 * Para alterar a conexão que será utilizada altere a Page Master.	 */			public static WebDriver getFirefoxDriver() {		FirefoxOptions opt = new FirefoxOptions();		WebDriver driver = null;		try {			driver = new RemoteWebDriver(new URL(Configs.URLSeleniumGrid),opt);		} catch (MalformedURLException e) {			e.printStackTrace();		}		return driver;	}	public static WebDriver getChromeDriver() {		ChromeOptions opt = new ChromeOptions();		WebDriver driver = null;		try {			driver = new RemoteWebDriver(new URL(Configs.URLSeleniumGrid),opt);		} catch (MalformedURLException e) {						e.printStackTrace();		}		return driver;	}		public static WebDriver getOperaDriver() {		OperaOptions opt = new OperaOptions();		WebDriver driver = null;		try {			driver = new RemoteWebDriver(new URL(Configs.URLSeleniumGrid),opt);		} catch (MalformedURLException e) {						e.printStackTrace();		}		return driver;	}		public static WebDriver getIEDriver() {		InternetExplorerOptions opt = new InternetExplorerOptions();		WebDriver driver = null;		try {			driver = new RemoteWebDriver(new URL(Configs.URLSeleniumGrid),opt);		} catch (MalformedURLException e) {						e.printStackTrace();		}		return driver;	}		public static WebDriver getEdgeDriver() {		EdgeOptions opt = new EdgeOptions();		WebDriver driver = null;		try {			driver = new RemoteWebDriver(new URL(Configs.URLSeleniumGrid),opt);		} catch (MalformedURLException e) {						e.printStackTrace();		}		return driver;	}	public static WebDriver getLocalChromeDriver() {		System.setProperty(Configs.NomeDriverChrome, Configs.ChromePath);		return new ChromeDriver();	}		public static WebDriver getLocalFireFoxDriver() {		System.setProperty(Configs.NomeDriverFirefox, Configs.FirefoxPath);		return new FirefoxDriver();	}		public static WebDriver getLocalIEDriver() {		System.setProperty(Configs.NomeDriverIE, Configs.IEPath);		return new InternetExplorerDriver();	}}