package utils;import org.openqa.selenium.JavascriptExecutor;import org.openqa.selenium.WebDriver;
public class BrowserInteraction {	public static void setUrl(WebDriver driver, String end){		driver.get(end);	}		public static void setMaxNav(WebDriver driver){		driver.manage().window().maximize();	}	public static void getAtualizaPag(WebDriver driver){		driver.navigate().refresh();	}		public static void setPagAnterior(WebDriver driver){		driver.navigate().back();	}	public static void setPagSeguinte(WebDriver driver){		driver.navigate().forward();	}		public static String getCodFontePag(WebDriver driver){		return driver.getPageSource();	}		public static String getUrlAtual(WebDriver driver){		return driver.getCurrentUrl();			}		public static void encerraNavegador (WebDriver driver){		driver.quit();	}		public static void pageRoll (WebDriver driver){		JavascriptExecutor jse = (JavascriptExecutor) driver;	    jse.executeScript("window.scrollBy(0,350)", "");	}		public static String getTxtAlert (WebDriver driver){		return driver.switchTo().alert().getText();	}		public static void setAceitaAlert (WebDriver driver){		driver.switchTo().alert().accept();			}		public static void setElementoAtivo (WebDriver driver){		driver.switchTo().activeElement();	}    }